'use strict';
var React = require('react');

var Form = require('./demo.jsx');


React.renderComponent(Form(), document.getElementById('react-main'));
/** @jsx React.DOM */'use strict';
var React = require('react');
var oboe = require('oboe');


module.exports  = React.createClass({
    displayName: 'DemoPage',

    getInitialState: function() {
        return {
            exchanges: []
        };
    },
    componentDidMount: function() {
        var that = this;

        oboe('http://localhost:3000/api/resource/exchange/jsoncsv')
           .node('*', function(arr) {
                if(Array.isArray(arr) && Array.isArray(arr[0])) {
                    arr = arr[0];
                    var o = {
                        marketName: arr[0],
                        securityName: arr[1],
                        currency: arr[2],
                        actionType: arr[3],
                        priceLevel: arr[4],
                        amount: parseFloat(arr[5])
                    };

                    console.log('received a fragment', o);

                    that.setState({exchanges: that.state.exchanges.concat([o])});
                }
           });
    },
    render: function() {
        var exchanges = this.state.exchanges;

        return (
            React.DOM.div(null, 
                React.DOM.h1(null, "Exchanges"), 

                React.DOM.table({className: "exchanges"}, 
                    React.DOM.thead(null, 
                        React.DOM.tr(null, 
                            React.DOM.th(null, "Market name"), 
                            React.DOM.th(null, "Security name"), 
                            React.DOM.th(null, "Currency"), 
                            React.DOM.th(null, "Action type"), 
                            React.DOM.th(null, "Price level"), 
                            React.DOM.th(null, "Amount")
                        )
                    ), 
                    React.DOM.tbody(null, 
                    exchanges.map(function(exchange, i) {
                        return React.DOM.tr({key: i}, 
                            React.DOM.td(null, exchange.marketName), 
                            React.DOM.td(null, exchange.securityName), 
                            React.DOM.td(null, exchange.currency), 
                            React.DOM.td(null, exchange.actionType), 
                            React.DOM.td(null, exchange.priceLevel), 
                            React.DOM.td(null, exchange.amount)
                        );
                    })
                    )
                )
            )
        );
    }
});
// This file is the concatenation of many js files.
// See http://github.com/jimhigson/oboe.js for the raw source

// having a local undefined, window, Object etc allows slightly better minification:                    
(function  (window, Object, Array, Error, JSON, undefined ) {

   // v2.0.1

